      console.log(res);
      for (const id in res.data) {
        personas.value.push({
          id: id,
          nombre: res.data[id].nombre,
          codigo: res.data[id].codigo,
          /* mail: res.data[id].email, */
          precio: res.data[id].precio
        });
      }
    }).catch(error => console.log(error));
    return {
      personas
    };
  }
